import React from 'react';
import { useEffect } from 'react';
import './Home.css';
//import Menu from '../components/Menu';
import Card from '../components/Card';
import axios from 'axios';
import { createStore, combineReducers } from 'redux';
import { Provider, connect } from 'react-redux';

const GET_WEATHER_REQUEST = 'GET_WEATHER_REQUEST';
const GET_WEATHER_SUCCESS = 'GET_WEATHER_SUCCESS';
const GET_WEATHER_FAILURE = 'GET_WEATHER_FAILURE';

const weatherReducer = (state: any = {}, action: any) => {
    switch (action.type) {
        case GET_WEATHER_REQUEST:
            return {
                ...state,
                loading: true,
                error: null,
            };
        case GET_WEATHER_SUCCESS:
            return {
                ...state,
                weather: action.weather,
                loading: false,
                error: null,
            };
        case GET_WEATHER_FAILURE:
            return {
                ...state,
                loading: false,
                error: action.error,
            };
        default:
            return state;
    }
};

// Crear el store de Redux con el reducer
const store = createStore(weatherReducer);

const getWeather = (lat: number, lon: number) => {
    return async (dispatch: any) => {
        dispatch({ type: GET_WEATHER_REQUEST });
        try {
            const API_KEY = 'a65b5fd24a9d9ca4df20159e4c44bd29';
            const API_ENDPOINT = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${API_KEY}`;
            const response = await axios.get(API_ENDPOINT);
            dispatch({ type: GET_WEATHER_SUCCESS, weather: response.data });
        } catch (error) {
            dispatch({ type: GET_WEATHER_FAILURE, error });
        }
    };
};


function Home() {

    useEffect(() => {
        const fetchData = async () => {
            // Llamar a la función getWeather con las coordenadas deseadas
            getWeather(2.4422295, -76.6072368)(store.dispatch);
        };
        fetchData();
    }, []);

    return (

        <div >

            <div className='header'>

                <div>
                    <img className="logo" src="https://lh3.googleusercontent.com/1NWSffUULql2uPj0hT_WTwSTfmUv0yuXk0Rq0Q6tpcUZbMurPESyK8W2NBqpa8SRpA=w300" alt="" />
                </div>

                <h1 className="title">Página del Clima</h1>

                <form className="search-form">

                    <input type="text" placeholder="Buscar..." />

                    <button type="submit">Buscar</button>

                </form>

            </div>

            <div className='body'>

                <div className='cards-container'>
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                </div>

            </div>

        </div>
    );
}

export default Home;
