import React from 'react';
import logo from './logo.svg';
import './Card.css';

function Card(props: any) {

    const [checked, setChecked] = React.useState(false);

    const handleChange = () => {
        setChecked(!checked);
    };


    return (

        <div className="card">
            <label className="switch">
                <input type="checkbox"
                    checked={checked}
                    onChange={handleChange}
                />
                
                <span className="slider round"></span>
            </label>

            <div className="title">Ciudad: {props.ciudad}</div>
            <div className="title">Estado: {props.estado}</div>
            <div className="title">Pais: {props.pais}</div>
            {checked &&
                <div className="title">Temperatura: {((props.temperatura - 273.15) * 9 / 5 + 32).toFixed(1)}°F</div>
            }
            {!checked &&
                <div className="title">Temperatura: {((props.temperatura - 273.15)).toFixed(1)}°C</div>
            }

            <div className="title">Humedad: {props.humedad}</div>
            <div className="title">Velocidad de viento: {props.windSpeed}</div>

        </div>

    );
}

export default Card;
