import React, { useEffect } from 'react';
import logo from './logo.svg';
import './Card.css';

function Card(props: any) {

    const [checked, setChecked] = React.useState(false);
    const [grados, setGrados] = React.useState("°C");

    const handleChange = () => {
        setChecked(!checked);
        if(!checked){
            setGrados("°F")
        }else{
            setGrados("°C")
        }
    };

    useEffect(() => {
        
      }, [props.timezone])
      
    function calcTime() {
        // create Date object for current location
        var d = new Date();
    
        // convert to msec
        // subtract local time zone offset
        // get UTC time in msec
        console.log(props.timezone)
        var utc = d.getTime() + (props.timezone*60000);
        console.log(utc)
        // create new Date object for different city
        // using supplied offset
        var nd = new Date(utc + (3600000));
    
        // return time as a string
        return nd;
    }
    
    return (

        <div className="card">
            <label className="switch">
                <input type="checkbox"
                    checked={checked}
                    onChange={handleChange}
                />
                <span className="slider round">
                    <span className="moving-letter">{grados}</span>                    
                </span>
            </label>

            <div className="title">Ciudad: {props.ciudad}, {props.pais}</div>
            <div className="title">Clima: {props.estado}</div>
            <div className="title">Hora: {calcTime().toLocaleTimeString()}</div>
            {checked &&
                <div className="title">Temperatura: {((props.temperatura - 273.15) * 9 / 5 + 32).toFixed(1)}°F</div>
            }
            {!checked &&
                <div className="title">Temperatura: {((props.temperatura - 273.15)).toFixed(1)}°C</div>
            }

            <div className="title">Humedad: {props.humedad}%</div>
            <div className="title">Velocidad de viento: {props.windSpeed}m/s</div>

        </div>

    );
}

export default Card;
