// actions.ts
import axios from 'axios';

export const FETCH_WEATHER_DATA_START = 'FETCH_WEATHER_DATA_START';
export const FETCH_WEATHER_DATA_SUCCESS = 'FETCH_WEATHER_DATA_SUCCESS';
export const FETCH_WEATHER_DATA_FAILURE = 'FETCH_WEATHER_DATA_FAILURE';

export const fetchWeatherData = () => {
  return async (dispatch:any) => {
    dispatch({ type: FETCH_WEATHER_DATA_START });
    try {
      const apiKey = 'a65b5fd24a9d9ca4df20159e4c44bd29';
      const lat = 2.4422295;
      const lon = -76.6072368;
      const city = 'your_city';
      const units = 'metric';
      const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apiKey}`;
      const response = await axios.get(url);
      const data = response.data;
      const weatherData = {
        humidity: data.main.humidity,
        temperature: data.main.temp,
        windSpeed: data.wind.speed,
      };
      dispatch({ type: FETCH_WEATHER_DATA_SUCCESS, payload: weatherData });
    } catch (error) {
      dispatch({ type: FETCH_WEATHER_DATA_FAILURE, payload: error });
    }
  };
};
