import React, { useRef, useState } from 'react';
import { useEffect } from 'react';
import './Home.css';
//import Menu from '../components/Menu';
import Card from '../components/Card';
import { weatherApi } from '../Api/WeatherMapApi';



function Home(props: any) {
    
    const [search, setSearch] = useState("");
    const searchInput = useRef(null);

    function handleSearch() {
        setSearch(searchInput.current.value);
    }

    const [cityName, setCityName] = useState({name:"Popayan"})
    const {data, isFetching} = weatherApi.endpoints.getCityByName.useQuery(cityName.name);
    console.log(data);
    return (
        
        <div >

            <div className='header'>

                <div>
                    <img className="logo" src="https://lh3.googleusercontent.com/1NWSffUULql2uPj0hT_WTwSTfmUv0yuXk0Rq0Q6tpcUZbMurPESyK8W2NBqpa8SRpA=w300" alt="" />
                </div>

                <h1 className="title">Página del Clima</h1>

                <form className="search-form">

                    <input type="text" placeholder="Buscar..." value={search} ref={searchInput} onChange={handleSearch} />

                    <button type="submit">Buscar</button>

                </form>

            </div>

            <div className='body'>

                <div className='cards-container'>
                    <Card ciudad={data?.name} estado="" pais={data?.sys?.country}/>
                </div>

            </div>

        </div>
    );
}

export default (Home);
