import React, { useRef, useState } from 'react';
import { useEffect } from 'react';
import './Home.css';
//import Menu from '../components/Menu';
import Card from '../components/Card';
import { weatherApi } from '../Api/WeatherMapApi';



function Home(props: any) {
    
   

    const [cityName, setCityName] = useState("")
    const {data, isFetching} = weatherApi.endpoints.getCityByName.useQuery(cityName);
    const searchInput = useRef<HTMLInputElement>(null);
    
    function handleSearch() {
        if (searchInput.current != null) {
        setCityName(searchInput.current.value);
    }
    }
    console.log(data);
    return (
        
        <div >

            <div className='header'>

                <div>
                    <img className="logo" src="https://lh3.googleusercontent.com/1NWSffUULql2uPj0hT_WTwSTfmUv0yuXk0Rq0Q6tpcUZbMurPESyK8W2NBqpa8SRpA=w300" alt="" />
                </div>

                <h1 className="title">Página del Clima</h1>

                <form className="search-form">

                    <input type="text" placeholder="Ciudad, Estado, País..." value={cityName} ref={searchInput} onChange={handleSearch} />

                </form>

            </div>

            <div className='body'>

                <div className='cards-container'>
                    <Card 
                    ciudad={data?.name} 
                    estado={data?.weather[0]?.main}
                    pais={data?.sys?.country}
                    temperatura={data?.main?.temp}
                    humedad={data?.main?.humidity}
                    windSpeed={data?.wind?.speed}
                    
                    />
                </div>

            </div>

        </div>
    );
}

export default (Home);
